import React, { Component } from "react";
import "./style.css";
import Search from "../Search";

const employees = [
  {
    image:
      "https://vignette.wikia.nocookie.net/spongebobgalaxy/images/0/07/SpongeBob_SquarePants.png/revision/latest?cb=20171228024014",
    name: "Sponge Bob",
    phone: "215 521 4434",
    email: "imreadyimready@cartoon.net",
    dob: "10/12/1993"
  },
  {
    image:
      "https://vignette3.wikia.nocookie.net/vsbattles/images/8/80/Mr._Krabs.png/revision/latest?cb=20150919162131",
    name: "Eugene Krabs",
    phone: "814 355 6422",
    email: "moneykrabs@cartoon.net",
    dob: "10/12/1993"
  },
  {
    image:
      "https://vignette2.wikia.nocookie.net/fictionalcharacters/images/a/ac/Squidward.png/revision/latest?cb=20131121012626",
    name: "Squidward Tenticles",
    phone: "234 724 4325",
    email: "clarinetmaster666@cartoon.net",
    dob: "10/12/1993"
  },
  {
    image:
      "https://s-media-cache-ak0.pinimg.com/originals/fe/32/49/fe32495d45283cd6860ae122f0aeaad9.png",
    name: "Dexter",
    phone: "610 534 5822",
    email: "smartaf@cartoon.net",
    dob: "10/12/1993"
  }
];

export class EmployeeTable extends Component {
  state = {
    employees: employees,
    search: "",
    sortOrder: ""
  };

  handleInputChange = event => {
    if (event.target.name === "search") {
      const searchName = event.target.value.toLowerCase();
      console.log(searchName);

      const newName = this.state.employees.filter(item => {
        return item.name.toLowerCase().includes(searchName);
      });
      console.log(newName);

      this.setState({
        [event.target.name]: event.target.value,
        employees: newName
        });

    } else {
        this.setState({ 
        employees: employees
        })
    }
}

  sortByName = () => {
    console.log("click");
    let sortedEmployees = this.state.employees.sort((a, b) => {
      if (b.name > a.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });

    if (this.state.sortOrder === "DESC") {
      sortedEmployees.reverse();
      this.setState({ sortOrder: "ASC" });
    } else {
      this.setState({ sortOrder: "DESC" });
    }

    console.log(sortedEmployees);
    this.setState({ employees: sortedEmployees });
  };

  render() {
    return (
      <>
        <input
          type="text"
          id="searchBox"
          name="search"
          placeholder="Search Cartoons"
          value={this.state.search}
          onChange={this.handleInputChange}
        />
        <table>
          <tr>
            <th></th>
            <th>
              Name<button onClick={this.sortByName}>Sort</button>
            </th>
            <th>House</th>
            <th></th>
            <th>DOB</th>
          </tr>
          {this.state.employees.map(person => (
            <tr>
              <td>
                <img src={person.image} />
              </td>
              <td>{person.name}</td>
              <td>{person.phone}</td>
              <td>{person.email}</td>
              <td>{person.dob}</td>
            </tr>
          ))}
        </table>
      </>
    );
  }
}

export default EmployeeTable;
