import React, { Component } from "react";
import "./style.css";
import Search from "../Search";

const employees = [
  {
    image:
      "https://www.factinate.com/wp-content/uploads/2018/08/325117.jpg",
    name: "Eddard Stark",
    house: "Stark",
    pob: "Winterfell",
    status: "Beheading"

  },
  {
    image:
    "https://www.telegraph.co.uk/content/dam/fashion/2017/06/19/TELEMMGLPICT000131421802_trans%2B%2BkUE_BTgBOQu3VWKvpDGX9fr7sARQy7EgBjwPUzvqL_M.jpeg",
  name: "Daeneryes Targaryen",
  house: "Targaryen",
  pob: "Dragonstone",
  status: "Poor Writing"
  },
  {
    image:
      "https://i.pinimg.com/474x/3f/22/86/3f2286861047770bce7b2d5347288ea3--dragons-game-of-thrones-games-of-thrones.jpg",
    name: 'Aegon Targaryen',
    house: "Targaryen",
    pob: "Winterfell",
    status: "ALIVE!"
  },
  {
    image:
    "https://i.insider.com/59a5dd9cb065da49008b4878?width=1100&format=jpeg&auto=webp",
  name: 'Tyrion Lan',
  house: "Targaryen",
  pob: "Winterfell",
  status: "ALIVE!"
  }
];

export class EmployeeTable extends Component {
  state = {
    employees: employees,
    search: "",
    sortOrder: ""
  };

  handleInputChange = event => {
    if (event.target.name === "search") {
      const searchName = event.target.value.toLowerCase();
      console.log(searchName);

      const newName = this.state.employees.filter(item => {
        return item.name.toLowerCase().includes(searchName);
      });
      console.log(newName);

      this.setState({
        [event.target.name]: event.target.value,
        employees: newName
        });

    } else {
        this.setState({ 
        employees: employees
        })
    }
}

  sortByName = () => {
    console.log("click");
    let sortedEmployees = this.state.employees.sort((a, b) => {
      if (b.name > a.name) {
        return -1;
      }
      if (a.name > b.name) {
        return 1;
      }
      return 0;
    });

    if (this.state.sortOrder === "DESC") {
      sortedEmployees.reverse();
      this.setState({ sortOrder: "ASC" });
    } else {
      this.setState({ sortOrder: "DESC" });
    }

    console.log(sortedEmployees);
    this.setState({ employees: sortedEmployees });
  };

  render() {
    return (
      <>
        <input
          type="text"
          id="searchBox"
          name="search"
          placeholder="Search Cartoons"
          value={this.state.search}
          onChange={this.handleInputChange}
        />
        <table>
          <tr>
            <th></th>
            <th>
              Name<button onClick={this.sortByName}>Sort</button>
            </th>
            <th>House</th>
            <th>Place of Birth</th>
            <th>Death</th>
          </tr>
          {this.state.employees.map(person => (
            <tr>
              <td>
                <img src={person.image} />
              </td>
              <td>{person.name}</td>
              <td>{person.house}</td>
              <td>{person.pob}</td>
              <td>{person.status}</td>

            </tr>
          ))}
        </table>
      </>
    );
  }
}

export default EmployeeTable;
